import React, { useState } from 'react';
import aboutData from '../data/about.json';

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the form data to your backend
    console.log('Form submitted:', formData);
    alert('Thank you for your message! I\'ll get back to you soon.');
    setFormData({ name: '', email: '', subject: '', message: '' });
  };

  return (
    <div className="container" style={{ paddingTop: 'var(--spacing-2xl)' }}>
      {/* Hero Section */}
      <section className="neumorphic-raised" style={{ 
        padding: 'var(--spacing-2xl)', 
        marginBottom: 'var(--spacing-2xl)',
        textAlign: 'center'
      }}>
        <h1 className="text-embossed" style={{ 
          fontSize: '3rem', 
          marginBottom: 'var(--spacing-lg)',
          fontFamily: 'var(--font-display)'
        }}>
          Let's Connect
        </h1>
        <p style={{ 
          fontSize: '1.3rem', 
          lineHeight: 1.7,
          maxWidth: '600px',
          margin: '0 auto',
          color: 'var(--text-secondary)'
        }}>
          Whether you have a project in mind, want to collaborate, or just want to say hello‚ÄîI'd love to hear from you.
        </p>
      </section>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 'var(--spacing-2xl)' }}>
        {/* Contact Form */}
        <section className="neumorphic-raised" style={{ padding: 'var(--spacing-2xl)' }}>
          <h2 className="text-embossed" style={{ 
            fontSize: '2rem', 
            marginBottom: 'var(--spacing-xl)'
          }}>
            Send a Message
          </h2>
          
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-lg)' }}>
            <div>
              <label htmlFor="name" style={{ 
                display: 'block', 
                marginBottom: 'var(--spacing-sm)',
                fontWeight: 'bold'
              }}>
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                className="neumorphic-input"
              />
            </div>
            
            <div>
              <label htmlFor="email" style={{ 
                display: 'block', 
                marginBottom: 'var(--spacing-sm)',
                fontWeight: 'bold'
              }}>
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                className="neumorphic-input"
              />
            </div>
            
            <div>
              <label htmlFor="subject" style={{ 
                display: 'block', 
                marginBottom: 'var(--spacing-sm)',
                fontWeight: 'bold'
              }}>
                Subject
              </label>
              <input
                type="text"
                id="subject"
                name="subject"
                value={formData.subject}
                onChange={handleChange}
                required
                className="neumorphic-input"
              />
            </div>
            
            <div>
              <label htmlFor="message" style={{ 
                display: 'block', 
                marginBottom: 'var(--spacing-sm)',
                fontWeight: 'bold'
              }}>
                Message
              </label>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                required
                rows="6"
                className="neumorphic-input"
                style={{ resize: 'vertical' }}
              />
            </div>
            
